const minusBtn = document.getElementById('quantity-minus');
const plusBtn = document.getElementById('quantity-plus');
const quantity = document.getElementById('quantity-value');
const select = document.getElementById('selection');
const miniCart = document.getElementById('cart-mini');
const createdMiniCart = document.getElementById('cart-mini-element1');
const resultQuantity = document.getElementById('resultQuantity');
const resultCheck = document.getElementById('resultCheck');
const createdMiniCart2 = document.getElementById('cart-mini-element2');

//수량 플러스, 마이너스 버튼
minusBtn.addEventListener('click', () => {
    if (quantity.value == 1) {
        alert("최소 주문 수량은 1개입니다.")
    }
    else {
        quantity.value --
    }
})

plusBtn.addEventListener('click', () => {
    if (quantity.value >= 10) {
        alert("개인 최대 구매수량은 10개입니다.")
        quantity.value = 10
    }
    else {
        quantity.value ++
    }
})

// 담기 버튼
miniCart.addEventListener('click', () => {
    if (select.options[select.selectedIndex].text == "선택하세요.") {
        alert("선택 먼저하세요")
    }
    else {
        createdMiniCart.innerHTML = `<small class="fw-bold">${select.options[select.selectedIndex].text}</small>
        <small class="fw-bold">${quantity.value}개</small>`;
        resultQuantity.innerText = `${quantity.value}개`;
        resultCheck.innerText = (15000 * quantity.value).toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",")+"원";
    }
})

